import Modal from "react-modal/lib/components/Modal";
import CloseIcon from "../../../assets/close.png"
import "./style.css"
import { useState } from "react";


function EnderecoModal(props){

  const [endereco, setEndereco] = useState('');
  const [complemento, setComplemento] = useState('');
  const [bairro, setBairro] = useState('');

  function SalvarCidade(e) {
    const [cid, est] = e.target[e.target.selectedIndex].text.split(" - ");
    setCidade(cid);
    setUF(est);
    setCodCidade(e.target.value);
  }
  return <Modal isOpen={props.isOpen}
                onRequestClose={props.onRequestClose} 
                overlayClassName="react-modal-overlay" 
                className="react-modal-content">

    <button type="button" onClick={props.onRequestClose} className="react-modal-close">
      <img src={CloseIcon} alt="" />  
    </button>

    <div className="container-fluid h-100 endereco">
      <div className="col-12 mt-4">
        <h4>Editar Endereço</h4>

        <form>
          <div className="row">
            <div className="mb-3 col-8 d-inline-block">
              <label htmlFor="InputNome" className="form-label mb-1">Endereços</label>
              <input type="text" onChange={(e) => setEndereco(e.target.value)} className="form-control mb-2" id="InputNome" aria-describedby="nome"/>
            </div>
            <div className="mb-3 col-4 d-inline-block">
              <label htmlFor="InputNome" className="form-label mb-1">Compl.</label>
              <input type="text" onChange={(e) => setComplemento(e.target.value)} className="form-control mb-2" placeholder="ex: Ap 63" id="InputNome" aria-describedby="nome"/>
            </div>
          </div>

          <div className="row">
            <div className="mb-3 col-12">
              <label htmlFor="InputNome" className="form-label mb-1">Bairro</label>
              <input type="text" onChange={(e) => setBairro(e.target.value)} className="form-control mb-2" id="InputNome" aria-describedby="nome"/>
            </div>
          </div>

          <div className="col-lg-6">
                <div className="form-control mb-2">
                  <select name="cidades" id="cidades" onChange={SalvarCidade}>
                    <option value="00000000">Cidades</option>
                    {cidades.map((c) => {
                      return (
                        <option key={c.codCidade} value={c.codCidade}>
                          {c.cidade} - {c.uf}
                        </option>
                      );
                    })}
                  </select>
                </div>
              </div>
        </form>
      </div>
    </div> 
  </Modal>
}

export default EnderecoModal;
