import "./style.css";
import {Dock} from 'react-dock';

function Sidebar(){

  const show = true;

  return <Dock position="right" isVisible={show} onVisibleChange={(visible) => show = visible}>
    <div className="container-fluid h-100 pt-4 sidebar">
      <h5>Minha Sacola</h5>

      <div className="row produtos">
        {
          [1, 2, 3, 4, 5].map(produto => {
            return <h4>Produto xxxx</h4>
          })
        }
      </div>
      <div className="row align-items-end footer">
        <div className="col-12 d-flex justify-content-between align-items-center">
          <span>Subtotal</span>
          <span>R$45.50</span>
        </div>
        <div className="col-12 d-flex justify-content-between align-items-center mt-2">
          <div class="input-group">
            <input type="text" class="form-control" placeholder="Cupom" aria-label="Recipient's username" aria-describedby="button-addon2" />
            <button class="btn btn-outline-sucess" type="button" id="button-addon2">Applicar</button>
          </div>
            <div class="input-group justify-content-end">
              <span>R$</span>
            </div>
        </div>
      </div>
    </div>
  </Dock>
}

export default Sidebar;
