import { createContext, useEffect, useState } from "react";

  const SacolaContext = createContext({});

  function SacolaProvider(props){
    const item = [{
      idCarrinho: 1,
      idProduto: 123,
      nome: "Pizza",
      qtd: 2,
      vlUnitario: 45,
      urlFoto: "https://www.habibs.com.br/storage/products/images/12475_grid_fundo_mobile.png",
      detalhes: []
    },
    {
      idCarrinho: 2,
      idProduto: 123,
      nome: "PizzaABC",
      qtd: 3,
      vlUnitario: 35,
      urlFoto: "https://www.habibs.com.br/storage/products/images/12475_grid_fundo_mobile.png",
      detalhes: []
    }]

    const [sacola, setSacola] = useState(item);
    const [subtotalSacola, setSubtotalSacola] = useState(0);
    const [descontoSacola, setDescontoSacola] = useState(0);
    const [entregaSacola, setEntregaSacola] = useState(0);  
    const [idCupomSacola, setIdCupomSacola] = useState(0);  
    const [totalSacola, setTotalSacola] = useState(0);  
    const [idEstabelecimento, setIdEstabelecimento] = useState(0);  

    useEffect(() => {
      let soma = sacola.reduce((a, b) => a + (b.vlUnitario * b.qtd), 0);

      setSubtotal(soma);
    }, [sacola]);

    useEffect(() => {

      setTotal(subtotal - desconto + entrega);
    }, [subtotal, desconto, entrega]);

    return <SacolaContext.Provider value={{sacola, setSacola, subtotal, setSubtotal, desconto, setDesconto, 
                                          entrega, setEntrega, idCupom, setIdCupom, total, setTotal, idEstabelecimento, setIdEstabelecimento}}>
      {props.children}
    </SacolaContext.Provider>
  };

export {SacolaContext, SacolaProvider};
